import React, { Component } from 'react';
import axios from 'axios';
import TableRowCom from './tableRowCom';

export default class Completed extends Component {
  constructor(props) {
    super(props);
    this.state = {order: []};
  }
  componentDidMount(){
    console.log()
    this.getURL();
    this.interval = setInterval(() => {
      this.getURL();
    }, 3000)
  }

  getURL(data){
    axios.get('/order')
      .then(response => {
        console.log(response.data, "this is the object");
        response.data.map((objMapped, index) => {
          if(objMapped.item_actual > 1){
            this.setState({order: response.data.index});
          }
        })
      }).catch(function (error) {
      console.log(error)
    })
  }
  componentWillUnmount() {
    clearInterval(this.interval)
  }

  tabRowCom(){
    return this.state.order.map(function(object, i){
      return <TableRowCom obj={object} key={i} />
    })
  }
  render() {
    return (
      <div>
        <h3 align="center">Completed Orders</h3>
        <table className="table table-striped" style={{ marginTop: 20 }}>
          <thead>
          <tr>
            <th>Date</th>
            <th>Person</th>
            <th>Department</th>
            <th>Item</th>
            <th>Requested Weight</th>
            <th>Actual Weight</th>
          </tr>
          </thead>

          { this.tabRowCom() }

        </table>
      </div>
    )
  }
}
