import React, { Component } from 'react';

export default class Create extends Component {
  constructor(props){
    super(props);
    //this.fucntion = this.function.bind(this)

    this.state = {
      date: new Date(),
      person_name: '',
      item_added: '',
      weight: '',



    }
  }
  onChangePersonName(input){
    this.setState({
      person_name: input.target.value
    });
  }


  dateTime(){
    let today = new Date();
    //.padStart
    let dd = String(today.getDate());
    let mm = String(today.getMonth() + 1);
    let yyyy = today.getFullYear();
    today = mm + '/' + dd + '/' + yyyy
    return today;
  }
  clockTime(){
    let instant = new Date();
    let hour = String(instant.getHours() - 12);
    let min = String(instant.getMinutes()).padStart(2, '0');
    // let sec = String(instant.getSeconds());
    instant = hour + ':' + min;
    return instant;
  }

  render() {
    return (
      <div style={{ marginTop: 10 }}>
        <h3 align="center">Add an Order to the Que</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-row">
            <div className="form-group col-md mr-0">
              <label>Date of Order:  </label>
              <input
                //make this auto fill with date and time javascript
                type="text"
                className="form-control col-md mr-0"
                value={this.dateTime() + ' @ ' + this.clockTime() }
                // onChange={this.onChangeShiftDate}
              />
            </div>
            <div className="form-group col-md mr-0">
              <label>Name:  </label>
              <input
                id="inputState"
                type="text"
                className="form-control col-md mr-0"
                value=""
                onChange={this.onChangePersonName}
              />
            </div>
            <div className="form-group col-md ml-0">
              <label>Department: </label>
              <select id="inputState"
                      type="text"
                      className="form-control col-md ml-0"
                      // value={this.state.task_name}
                      // onChange={this.onChangeTaskName}
              >
                <option className="form-control">Department...</option>
                <option className="form-control">Meat</option>
                <option className="form-control">Seafood</option>
                <option className="form-control">Deli</option>
              </select>
            </div>
            <div className="form-group col-md ml-0">
              <label>Item to Order: </label>
              <select id="inputState"
                      type="text"
                      className="form-control"
                      // value={this.state.task_complete}
                      // onChange={this.onChangeTaskComplete}
              >
                {/*<option className="form-control">Was task completed...</option>*/}
                {/*<option className="form-control">Yes</option>*/}
                {/*<option className="form-control">No</option>*/}
              </select>
            </div>
            <div className="form-group col-md ml-0">
              <label>Quantity: </label>
              <input id="inputState"
                      type="text"
                      className="form-control"
                      value="lb's"
                      // onChange={this.onChangePercentComplete}
              >

              </input>
            </div>
          </div>
          <div className="form-row">
            <div className="form-group col-md mr-0">
              {/*<Label> Jon's Shift Notes:  </Label>*/}
              {/*<Input*/}
              {/*  type="textarea"*/}
              {/*  className="form-control col-md mr-0"*/}
              {/*  value={this.state.shift_notes}*/}
              {/*  onChange={this.onChangeShiftNotes}*/}
              {/*/>*/}
            </div>
          </div>
        </form>
      </div>
    )
  }
}
